import React, { useState, useEffect } from "react";
import {
  StyleSheet,
  View,
  Image,
  ActivityIndicator,
  FlatList,
} from "react-native";

import colors from "../config/colors";
import ListItem from "../components/ListItem";
import ListItemDeleteAction from "../components/ListItemDeleteAction";
import ListItemSeperator from "../components/ListItemSeperator";

import coinMarketDataApi from "../api/coinMarketDataApi";

import useApi from "../Hooks/useApi";

function TopListScreen({ navigation, route }) {
  const [refreshing, setRefreshing] = useState(false);
  const topCoins = ["bitcoin", "cardano", "bogged-finance"];

  const coinData = [];

  const getCoinMarketDataApi = useApi(
    coinMarketDataApi.getCoinMarketData,
    false
  );
  useEffect(() => {
    for (let i = 0; i <= topCoins.length; i++) {
      getCoinMarketDataApi.request("bitcoin");
      coinData[i] = getCoinMarketDataApi.data;
    }
  }, []);

  console.log(coinData);

  return (
    <View style={styles.container}>
      <View
        style={{
          backgroundColor: colors.background,
          flex: 1,
        }}
      >
        <View
          style={{
            backgroundColor: colors.background,
            flex: 7,
            alignItems: "center",
          }}
        >
          <View style={styles.listContainer}>
            {/* {getCoinMarketDataApi.error && (
              <>
                <Error>Couldn't Retrieve Data</Error>
                <AppButton title="Retry" onPress={loadCoinMarketData.data} />
                
              </>
            )}  
            */}

            <FlatList
              style={{ width: "100%" }}
              data={coinData}
              keyExtractor={(coin) => coin.symbol}
              renderItem={({ item }) => (
                <ListItem
                  title={item.symbol}
                  imageUrl={item.image}
                  size={48}
                  price={item.current_price}
                  oneHour={item.price_change_percentage_1h_in_currency}
                  twentyFourHours={item.price_change_percentage_24h_in_currency}
                  sevenDays={item.price_change_percentage_7d_in_currency}
                  thirtyDays={item.price_change_percentage_30d_in_currency}
                  onPress={() => console.log("Item selected", item)}
                  renderRightActions={() => (
                    <ListItemDeleteAction onPress={() => handleDelete(item)} />
                  )}
                />
              )}
              ItemSeparatorComponent={ListItemSeperator}
              refreshing={refreshing}
              onRefresh={() => {
                setCoins([
                  {
                    id: 2,
                    title: "UNI",
                    description: "27.30$",
                    image: require("../assets/coins/uni.png"),
                  },
                ]);
              }}
            />

            <ActivityIndicator
              animating={false}
              size="large"
              color={colors.light}
              style={{ marginTop: 20 }}
            />
          </View>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
  },

  listContainer: {
    width: "100%",
    alignItems: "center",
    justifyContent: "center",
  },

  image: {
    height: 40,
    width: 36,
  },
});

export default TopListScreen;
