import React from "react";
import { H2 } from "./AppText";

const apiUrl =
  "https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&ids=bitcoin&price_change_percentage=1h%2C24h%2C7d%2C30d";

async function ApiFetch() {
  const response = await fetch(apiUrl);
  const data = await response.json();

  let dataPrice, data1h, data24h, data7d, data30d;

  dataPrice = data[0].current_price;
  data1h = data[0].price_change_percentage_1h_in_currency;
  data24h = data[0].price_change_percentage_24h_in_currency;
  data7d = data[0].price_change_percentage_7d_in_currency;
  data30d = data[0].price_change_percentage_30d_in_currency;

  console.log(dataPrice, data1h, data24h, data7d, data30d);
  return { dataPrice, data1h, data24h, data7d, data30d };
}

function retard() {
  let a, b, c, d, e;

  a = dataPrice;

  console.log(dataPrice);
}

export { retard, ApiFetch };
