import React from 'react';
import { StyleSheet, Text, View, Image, SafeAreaView, Button, Alert, Platform, StatusBar } from 'react-native';




function WatchlistScreen(props) {
  return (
    
    <SafeAreaView style={styles.container}> {/*Hela sk√§rmen*/}
    <View style={{
      backgroundColor:"#FFFFFF",
      flex: 1,
      
    }}>
    
    {/*HEADER*/}
    <View style={{backgroundColor:"#0000FF", flex:1, }}> 
      
      {/*UPPER*/}
      <View style={{backgroundColor:"#0000FF", flex:3,width:"100%", justifyContent:"Center", alignItems:"Center"}}>
      
    <Text numberOfLines={1} onPress={handlePress} style={styles.title}>
      CoinCoin
    </Text>

    </View>
    {/*LOWER*/}
    <View style={{backgroundColor:"#00AAFF", flex:1,}}>
    </View>


    

    </View>
    {/*BODY*/}
    <View
    style={{backgroundColor:"#FFFFFF", flex:4,}}>

    
   

     {/*ITEM1*/}
    <View style={{flexDirection: "row", height:100, backgroundColor:"lightgray", marginHorizontal:20}}>
      {/*COIN AND TICKER*/}
      <View style={{flexDirection: "row", alignItems:"center", flex:1}}>
        <Image fadeDuration={1000} style={styles.coinPic} source={require("../assets/coins/ada.png")}/>
        <Text style={[styles.h1,styles.extraMargin]}>
        ADA
        </Text>
        <Text style={[styles.h1, styles.extraMargin]}>
        1.17$
      </Text>

      </View>
       {/*DATA*/}
      <View style={{flex:1}}>
      
      </View>


    </View>



    <Button style={styles.buttonStyle} title="Add coin" onPress={() => Alert.alert("Add coin", "Do you wish to add a coin?", [
      {text: "Yes"},
      {text: "No"},
    ]) }/>
    </View>

    </View>
   
   
   

    
  </SafeAreaView>


  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    paddingTop: Platform.OS === "android"? StatusBar.currentHeight : 0 //If its android, set it to 20 otherwise 0
  },
  
  
 
  title:{
    color:"#FFFFFF",
    fontFamily: "Montserrat",
    fontStyle: "normal",
    fontWeight: "700",
    fontSize:48,
    justifyContent:"center",
    alignItems:"center"
    
  },
  
  h1:{
    color:"#000000",
    fontFamily: "Montserrat",
    fontStyle: "normal",
    fontWeight: "700",
    fontSize:24,
    justifyContent:"center",
    alignItems:"center"
    
  },

  extraMargin:{
    marginLeft:15,
  
  },
  
  coinPic:{
    width: 64, 
    height: 64,
    
    
  },

  buttonStyle:{
    margin:100,
    backgroundColor:"#0000FF",
    width: "30%",
    height: 200,
  }
  ,
});


export default WatchlistScreen;
