import React from "react";

import { View, Image, StyleSheet, TouchableHighlight } from "react-native";

import colors from "../config/colors";
import { H1, H2, Percent } from "../components/AppText";
import Swipeable from "react-native-gesture-handler/Swipeable";

function ListItem({
  title,
  subTitle,
  image,
  size,
  onPress,
  renderRightActions,
}) {
  return (
    <Swipeable renderRightActions={renderRightActions}>
      <TouchableHighlight underlayColor={colors.light} onPress={onPress}>
        <View style={styles.containerStyle}>
          <View style={{ flexDirection: "row", alignItems: "center", flex: 3 }}>
            <Image
              fadeDuration={1000}
              style={{
                width: size,
                height: size,
                marginLeft: 30,
                marginRight: 10,
              }}
              source={image}
            />
            <H1>{title}</H1>
            <H1>{subTitle}</H1>
          </View>

          <View style={{ flex: 2, flexDirection: "row", paddingRight: 20 }}>
            <View style={{ flex: 1, alignItems: "center" }}>
              <H2>1h</H2>
              <Percent>+13%</Percent>
            </View>

            <View style={{ flex: 1, alignItems: "center" }}>
              <H2>24h</H2>

              <Percent>-27%</Percent>
            </View>

            <View style={{ flex: 1, alignItems: "center" }}>
              <H2>7d</H2>

              <Percent>+21%</Percent>
            </View>

            <View style={{ flex: 1, alignItems: "center" }}>
              <H2>30d</H2>

              <Percent>+130%</Percent>
            </View>
          </View>
        </View>
      </TouchableHighlight>
    </Swipeable>
  );
}

export default ListItem;

const styles = StyleSheet.create({
  coinPic: {
    marginLeft: 30,
    marginRight: 10,
  },

  containerStyle: {
    flexDirection: "row",
    width: "100%",
    height: 80,
    backgroundColor: colors.secondary,
    alignItems: "center",
  },
});
