import React, { useState, useEffect } from "react";
import { FlatList, StyleSheet, View } from "react-native";

import ListItem from "./ListItem";
import ListItemDeleteAction from "./ListItemDeleteAction";
import ListItemSeperator from "./ListItemSeperator";
import coinMarketData from "../api/coinMarketData";
import coinIDData from "../api/coinIDData";
import IconButton from "./IconButton";
import colors from "../config/colors";

function ItemList(props) {
  const [coinMarket, setCoinMarket] = useState([]);
  const [coinID, setCoinID] = useState([]);

  const [coins, setCoins] = useState([]);
  const [refreshing, setRefreshing] = useState(false);
  let userInputCoinID = "bitcoin";
  let coinExists = false;
  userInputCoinID = userInputCoinID.toLowerCase();

  useEffect(() => {
    loadCoinIDData();
  }, []);

  //Loading coin ID data
  const loadCoinIDData = async () => {
    const response = await coinIDData.getCoinIDData();
    setCoinID(response.data);
  };

  var coinName = "Inte fått värde";

  for (let i = 0; i < coinID.length; i++) {
    if (
      userInputCoinID === coinID[i].id ||
      userInputCoinID === coinID[i].symbol
    ) {
      coinExists = true;
      console.log(
        "Name: " + coinID[i].id + " " + "Ticker: " + coinID[i].symbol
      );
      coinName = coinID[i].id;
      break;
    }
  }

  coinName = "storm";

  const handleAdd = () => {
    console.log("Adding item..");
  };
  //Handle to Delete a coin from the list
  const handleDelete = (coin) => {
    setCoins(coins.filter((m) => m.id !== coin.id));
  };

  return (
    <View style={styles.styleContainer}>
      <FlatList
        style={{ width: "100%" }}
        data={coinMarket}
        keyExtractor={(coin) => coin.id.toString()}
        renderItem={({ item }) => (
          <ListItem
            title={item.symbol}
            imageUrl={item.image}
            size={48}
            price={item.current_price}
            oneHour={item.price_change_percentage_1h_in_currency}
            twentyFourHours={item.price_change_percentage_24h_in_currency}
            sevenDays={item.price_change_percentage_7d_in_currency}
            thirtyDays={item.price_change_percentage_30d_in_currency}
            onPress={() => console.log("Item selected", item)}
            renderRightActions={() => (
              <ListItemDeleteAction onPress={() => handleDelete(item)} />
            )}
          />
        )}
        ItemSeparatorComponent={ListItemSeperator}
        refreshing={refreshing}
        onRefresh={() => {
          setCoins([
            {
              id: 2,
              title: "UNI",
              description: "27.30$",
              image: require("../assets/coins/uni.png"),
            },
          ]);
        }}
      />

      <IconButton
        iconName={"pluscircle"}
        iconColor={colors.white}
        onPress={handleAdd}
      />
    </View>
  );
}

export default ItemList;

const styles = StyleSheet.create({
  styleContainer: {
    width: "100%",
    alignItems: "center",
    justifyContent: "center",
  },
});
