import React, { useState } from "react";
import {
  StyleSheet,
  View,
  Image,
  SafeAreaView,
  Platform,
  StatusBar,
} from "react-native";

import colors from "../config/colors";
import ItemList from "../components/ItemList";
import { Title } from "../components/AppText";
import IconButton from "../components/IconButton";
import AppTextInput from "../components/AppTextInput";
import AppPicker from "../components/AppPicker";

function WatchlistScreen(props) {
  {
    /* Creating categories for picker */
  }
  const categories = [
    { label: "Sort by price Rising", value: 1 },
    { label: "Sort by price Descending", value: 2 },
    { label: "Sort by market cap Rising", value: 3 },
    { label: "Sort by market cap Descending", value: 4 },
  ];

  const [selected, setSelected] = useState(false);

  console.log("App executed");
  const handlePress = () => console.log("Text pressed");

  return (
    <SafeAreaView style={styles.container}>
      <View
        style={{
          backgroundColor: colors.background,
          flex: 1,
        }}
      >
        <View style={{ backgroundColor: colors.primary, flex: 1 }}>
          <View
            style={{
              flex: 3,
              width: "100%",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <Title>CoinCoin</Title>
          </View>

          <View style={{ backgroundColor: "#00AAFF", flex: 1 }}></View>
        </View>

        <View
          style={{
            backgroundColor: colors.background,
            flex: 4,
            alignItems: "center",
          }}
        >
          <ItemList />

          <IconButton
            iconName={"pluscircle"}
            iconColor={colors.white}
            onPress={() => console.log("Tapped")}
          />

          <AppTextInput placeholder="test" iconName={"mail"} />

          <AppPicker
            items={categories}
            placeholder="category"
            iconName={"barschart"}
            selectedItem={selected}
            onSelectItem={(item) => setSelected(item)}
          />
        </View>
      </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0,
  },
});

export default WatchlistScreen;
