import coinMarketData from "./coinMarketData";
import React, { useState, useEffect } from "react";
import storeCoinID from "./storeCoinId";

function storeMarketData(props) {
  const [coinMarket, setCoinMarket] = useState([]);
  let arraytest = [];
  useEffect(() => {
    loadCoinMarketData();
  }, []);

  const coinID = storeCoinID(); //CoinID = arrayen med alla coins, de har tre properties id,symbol och name.

  //Loading marketdata
  const loadCoinMarketData = async () => {
    for (let i = 0; i < coinID.length; i++) {
      const marketResponse = await coinMarketData.getCoinMarketData(coinID[i]);
      arraytest[i] = marketResponse.data;
      console.log("CoinMarketen:", arraytest);
    }
  };

  return coinMarket;
}

export default storeMarketData;
