import React, { useEffect, useState } from "react";

import WatchlistScreen from "./app/screens/WatchlistScreen";
import LoginScreen from "./app/screens/LoginScreen";
import * as ImagePicker from "expo-image-picker";
import { Button, Image, View } from "react-native";
import ScreenArea from "./app/components/ScreenArea";

export default function App() {
  //State for the image
  const [imageUri, setImageUri] = useState();

  //Requesting permission to use media library
  const requestPermission = async () => {
    const { granted } = await ImagePicker.requestMediaLibraryPermissionsAsync();
    if (!granted) alert("You need to able permission to access gallery");
  };

  //Calling the request permission here as useEffect cant be in async
  useEffect(() => {
    requestPermission();
  }, []);

  //Selecting image
  const selectImage = async () => {
    try {
      const result = await ImagePicker.launchImageLibraryAsync();
      //If the user doesent cancell
      if (!result.cancelled) setImageUri(result.uri);
    } catch (error) {
      console.log("Error reading an image");
    }
  };
  //Calling the selectImage function on press
  return <WatchlistScreen />;
}
