import coinMarketData from "./coinMarketData";
import React, { useState, useEffect } from "react";
import storeCoinID from "./storeCoinId";

function storeMarketData(props) {
  useEffect(() => {
    loadCoinMarketData();
  }, []);

  const [coinMarket, setCoinMarket] = useState([]);

  //CoinID = arrayen med alla coins, de har tre properties id,symbol och name.

  //Loading marketdata

  const loadCoinMarketData = async () => {
    const coinID = storeCoinID();
    const marketResponse = await coinMarketData.getCoinMarketData("bitcoin");
    setCoinMarket(marketResponse.data);
    console.log("CoinMarketen:", coinMarket);
  };
  return coinMarket;
}

export default storeMarketData;
