import React, { useState } from "react";
import { AntDesign } from "@expo/vector-icons";
import {
  Button,
  FlatList,
  Modal,
  StyleSheet,
  Text,
  TouchableWithoutFeedback,
  View,
} from "react-native";
import colors from "../config/colors";
import PickerItem from "./PickerItem";
import { H3 } from "./AppText";

function AppPicker({
  iconName,
  items,
  placeholder,
  selectedItem,
  onSelectItem,
}) {
  const [modalVisible, setModalVisible] = useState(false);

  return (
    <>
      <TouchableWithoutFeedback onPress={() => setModalVisible(true)}>
        <View style={styles.styleContainer}>
          {iconName && (
            <AntDesign
              name={iconName}
              size={20}
              style={{ color: colors.primary, marginRight: 10 }}
            />
          )}
          {/*if the selectedItem is defined, render the selectedItem.label otherwise render the placeholder */}
          <H3> {selectedItem ? selectedItem.label : placeholder} </H3>
          <AntDesign
            name={"down"}
            size={20}
            style={{ color: colors.primary }}
          />
        </View>
      </TouchableWithoutFeedback>
      {/* Modal visible uses bolean, true = visible, false = invisible */}
      <Modal visible={modalVisible} animationType="slide">
        <Button title="close" onPress={() => setModalVisible(false)} />
        <FlatList
          data={items}
          keyExtractor={(item) => item.value.toString()}
          renderItem={({ item }) => (
            <PickerItem
              label={item.label}
              onPress={() => {
                setModalVisible(false);
                onSelectItem(item);
              }}
            />
          )}
        />
      </Modal>
    </>
  );
}

export default AppPicker;

const styles = StyleSheet.create({
  styleContainer: {
    backgroundColor: colors.light,
    flexDirection: "row",
    width: "100%",
    padding: 20,

    alignItems: "center",
  },
});
