import React, { useState } from "react";
import { FlatList, StyleSheet, View } from "react-native";

import ListItem from "./ListItem";
import ListItemDeleteAction from "./ListItemDeleteAction";
import ListItemSeperator from "./ListItemSeperator";
import ApiFetch from "./ApiFetch";

var apifetch;

const callback = (apifetch) => {
  console.log("y=", apifetch);
};

const x = async () => {
  return Promise.resolve(
    await ApiFetch().then((apifetch) => callback(apifetch))
  );
};

console.log("x=", x);

const initialCoins = [
  {
    id: 1,
    title: "BTC",
    image: require("../assets/coins/btc.png"),
    size: 48,
  },

  {
    id: 2,
    title: "ADA",
    image: require("../assets/coins/ada.png"),
    size: 48,
  },

  {
    id: 3,
    title: "RVN",
    image: require("../assets/coins/rvn.png"),
    size: 48,
  },
];

async function ItemList(props) {
  const [coins, setCoins] = useState(initialCoins); //Array with two entries, array[0]=count array[1]=setCount
  const [refreshing, setRefreshing] = useState(false);

  const handleDelete = (coin) => {
    //Delete the coin from the list

    setCoins(coins.filter((m) => m.id !== coin.id));
  };

  return;
  await ApiFetch().then((t) => {
    return (
      <View style={styles.styleContainer}>
        <FlatList
          style={{ width: "100%" }}
          data={coins}
          keyExtractor={(coin) => coin.id.toString()}
          renderItem={({ item }) => (
            <ListItem
              title={item.title}
              image={item.image}
              size={item.size}
              price={t[0]}
              oneHour={item.oneHour}
              twentyFourHours={item.twentyFourHours}
              sevenDays={item.sevenDays}
              thirtyDays={item.thirtyDays}
              onPress={() => console.log("Item selected", item)}
              renderRightActions={() => (
                <ListItemDeleteAction onPress={() => handleDelete(item)} />
              )}
            />
          )}
          ItemSeparatorComponent={ListItemSeperator}
          refreshing={refreshing}
          onRefresh={() => {
            setCoins([
              {
                id: 2,
                title: "UNI",
                description: "27.30$",
                image: require("../assets/coins/uni.png"),
              },
            ]);
          }}
        />
      </View>
    );
  });
}

export default ItemList;

const styles = StyleSheet.create({
  styleContainer: {
    width: "100%",
    alignItems: "center",
    justifyContent: "center",
  },
});
