import client from "./client";

const endpoint =
  "/coins/markets?vs_currency=usd&ids=bitcoin&price_change_percentage=1h%2C24h%2C7d%2C30d";

//const coinID =  "bitcoin" ;

const getCoinData = () =>
  fetch(
    "https://api.coingecko.com/api/v3coins/markets?vs_currency=usd&ids=bitcoin&price_change_percentage=1h%2C24h%2C7d%2C30d"
  );

export default {
  getCoinData,
};
